pragma solidity ^0.4.24;

import "node_modules/openzeppelin-solidity/contracts/ownership/Ownable.sol";

contract Signer is Ownable {

	address[] signerIndex; //list of signer keys (address)

	enum RoleType {lawyer, beneficiary, codeAudit, granter}

	struct SignerStruct {
		RoleType roleType;//role could be owner, beneficiairy,lawyer,codeAudit
		uint index; //pointer of keys position in signerIndex
	}


	mapping(address => SignerStruct) signerStructs;//associate signer address to signer struct

	event SignerAdd(address signer, RoleType role);
	event SignerRemove(address addr);
	event SignerUpdate(address signer, RoleType role);

	/// @dev Allows to add a new signer to a role. Transaction has to be sent by wallet.
    /// @param role label the role of the signer. is he beneficiairy or lawyer or something else?
    /// @param signer Address of new signer.
	function addSigner(address signer, RoleType role) public returns(bool success) {
		//need to check if signer doesnt exist yet
		require(!isSigner(signer),"Signer already exist");
		RoleType testrole = role;
		signerStructs[signer].roleType = testrole;
		signerIndex.push(signer);
		emit SignerAdd(signer, testrole);
		return true;
	}

	///@dev retrieve a signer by address, returns the signer's role type
	/// @param signer Address of the signer.
	function getSigner(address signer) public constant returns(RoleType roleType) {
		return (
			signerStructs[signer].roleType
		);
	}

	///@dev updates the role type of a signer
	function updateSigner(address signer, RoleType role) public returns(bool success) {
		//need to check if signer exist
		require(isSigner(signer),"Signer does not exist");
		signerStructs[signer].roleType = role;
		emit SignerUpdate(signer, role);
		return true;
	}

	function isSigner(address signer) public constant returns(bool isIndeed) {
		//if signerIndex array is empty, this signer doesnt exist
		if (signerIndex.length == 0) return false;
		//get the index of the signer to look up its address
		return(signerIndex[signerStructs[signer].index] == signer);
	}

	function getSignerCount() public constant returns(uint count) {
		return signerIndex.length;
	}

	function getSignerAtIndex(uint index) public constant returns(address signer) {
	  return signerIndex[index];
	}

	/// @dev Allows to remove an signer. Transaction has to be sent by wallet.
    /// @param signer Address of owner.
	function removeSigner(address signer) onlyOwner public returns(bool success){
		//need to check if signer exist
		require(isSigner(signer),"Signer does not exist");

		uint rowToDelete = signerStructs[signer].index;
		address keyToMove = signerIndex[signerIndex.length - 1];
		signerIndex[rowToDelete] = keyToMove;
		signerStructs[keyToMove].index = rowToDelete;
		signerIndex.length-- ; 

        emit SignerRemove(signer);
        return true;
	}

}

